   integrity_error  exception;
   errno            integer;
   errmsg           char(200);
   dummy            integer;
   found            boolean;
   seq              number;
   mutating         exception;
   PRAGMA exception_init(mutating, -4091);

   --  Dichiarazione UpdateChildParentExist constraint per la tabella "CODICI_TRIBUTO"
   cursor cpk1_categorie(var_tributo number) is
