   end if;

   begin  -- Check REFERENTIAL Integrity on INSERT or UPDATE
      if UPDATING then
         PRATICHE_TRIBUTO_PU(:OLD.PRATICA,
                             :OLD.COD_FISCALE,
                             :OLD.TIPO_TRIBUTO,
                             :OLD.TIPO_CARICA,
