                             :OLD.IMPORTO_RIDOTTO_2,
                             :OLD.TIPO_ATTO,
                             :OLD.DOCUMENTO_ID,
                             :OLD.DOCUMENTO_MULTI_ID,
                             :OLD.TIPO_NOTIFICA,
