                                             :new.DAL);
                  fetch cpk_storico_soggetti into dummy;
                  found := cpk_storico_soggetti%FOUND;
                  close cpk_storico_soggetti;
                  if found then
                     errno  := -20007;
                     errmsg := 'Identificazione "'||
                               :new.NI||' '||
                               :new.DAL||
                               '" gia'' presente in Storico Soggetti. La registrazione  non puo'' essere inserita.';
