            :new.cognome_nome := :new.cognome||'/'||:new.nome;
         END IF;
      END IF;
      :new.cognome_nome_ric := upper(:new.cognome_nome);
      :new.cognome_ric      := upper(:new.cognome);
      :new.nome_ric         := upper(:new.nome);

